import React, { useEffect, useState } from 'react'

const Index = () => {

    const [users, setUsers] = useState([]);



    const getUsers = async () => {
        const response = await fetch("https://api.github.com/users");
        const FinalData = await response.json();
        setUsers(FinalData)
    }



    useEffect(() => {
        getUsers();
    }, [])

    const handleCardClick = (index) => {
        const updatedUsers = [...users];
        updatedUsers[index].isFlipped = !updatedUsers[index].isFlipped;
        setUsers(updatedUsers);
    };

    return (
        <div className="container">
            {users.map((curElem, index) => (
                <div className={`card_item ${curElem.isFlipped ? "flipped" : ""}`} key={curElem.id} onClick={() => handleCardClick(index)}>
                    <div className="card_inner">
                        <div className={`card-front ${curElem.isFlipped ? "hidden" : ""}`}>
                            <img src={curElem.avatar_url} alt="" />
                            <div className="userName">{curElem.login}</div>
                            <div className="userUrl">{curElem.url}</div>
                            <div className="detail-box">
                                <div className="gitDetail"><span>Articles</span>23</div>
                                <div className="gitDetail"><span>Following</span>45</div>
                                <div className="gitDetail"><span>Followers</span>11</div>
                            </div>
                            <button className="seeMore">See More</button>
                        </div>
                        <div className={`card-back ${curElem.isFlipped ? "" : "hidden"}`}>
                            {/* Content for the back of the card */}
                            {/* Add any content you want to display on the back of the card here */}
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default Index;
